import logo from './logo.svg';
import './App.css';
import Nav from './components/navBar'
import {Routes,Route} from 'react-router-dom';
import { Login } from "./pages/login";
import ItemContainer from "./pages/Item"
import React, { useState } from "react";
import { Register } from "./pages/register"

function App() {

  const [currentForm, setCurrentForm] = useState('login');
  const [islogin,setlogin]=useState(false)
  const toggleForm = (formName) => {
    setCurrentForm(formName);
  }
  return (
    <div className="App">
      // <Routes>
      //     <Route path = "/" element ={<Login/>}>
      //       {islogin ? (<Route index element={<ItemContainer />} />) : (<Route index element={<Login setlogin={setlogin}/>} />)}
      //     </Route>

      // </Routes>

    { islogin ? <ItemContainer/> : (
      currentForm === "login" ? <Login onFormSwitch={toggleForm}  setlogin={setlogin}/>  : <Register onFormSwitch={toggleForm} />)
      }

    </div>
  );
}

export default App;
